text, sort, parent_id
)
values (#{dictionaryItemId,jdbcType=VARCHAR},
#{dictionaryId,jdbcType=VARCHAR}, #{value,jdbcType=VARCHAR},
#{text,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER},
#{parentId,jdbcType=VARCHAR}
)
